C++<pre><code>#include "tgaimage.h"
using namespace std;

const TGAColor white = TGAColor(255, 255, 255, 255);
const TGAColor red = TGAColor(255, 0, 0, 255);
const TGAColor green = TGAColor(0, 255, 0, 255);
const TGAColor blue = TGAColor(0, 0, 255, 255);

// Bresenham
void line5(int x0, int y0, int x1, int y1, TGAImage&amp; image, TGAColor color) {
    bool steep;
    if (abs(y1 - y0) &gt; abs(x1 - x0)) {
        swap(x0, y0);  // 沿x=y对称
        swap(x1, y1);
        steep = true;
    }
    if (x0 &gt; x1) {
        swap(x0, x1);  // 交换两点
        swap(y0, y1);
    }

    int dx = x1 - x0;
    int dy = y1 - y0;
    int derror = abs(dy);
    int error = 0;
    int y = y0;
    if (!steep) {
        for (int x = x0; x &lt;= x1; x++) {
            image.set(x, y, color);
            error += derror;
            if (error &gt;= dx) {
                y += (y1 &gt; y0) ? 1 : -1;
                error -= dx * 2;
            }
        }
    } else {
        for (int x = x0; x &lt;= x1; x++) {
            image.set(y, x, color);
            error += derror;
            if (error &gt;= dx) {
                y += (y1 &gt; y0) ? 1 : -1;
                error -= dx * 2;
            }
        }
    }
}

int main(int argc, char** argv) {
    TGAImage image(100, 100, TGAImage::RGB);
    image.set(52, 41, red);
    line1(13, 20, 80, 40, image, white);
    line2(13, 20, 40, 80, image, blue);
    line3(33, 20, 70, 80, image, blue);
    line4(43, 20, 80, 80, image, green);
    line5(53, 20, 90, 80, image, green);
    image.flip_vertically();
    image.write_tga_file("output.tga");
    return 0;
}
</code></pre>

